import React, { useEffect, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import {
  AppBar,
  IconButton,
  Toolbar,
  Collapse,
  Typography,
} from "@material-ui/core";
import SortIcon from "@material-ui/icons/Sort";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
// import { Link as Scroll } from "react-scroll";
import Button from "@material-ui/core/Button";
import InfoIcon from "@material-ui/icons/Info";
import StatesOfIndia from "../components/StatesOfIndia";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
    fontFamily: "Nunito",
  },
  appbar: {
    background: "none",
  },
  appbarWrapper: {
    width: "80%",
    margin: "0 auto",
  },
  appbarTitle: {
    flexGrow: "1",
  },
  changeColor: {
    display: "flex",
    position: "relative",
    height: "100%",
    width: "100%",
    backgroundImage:
      "linear-gradient(-45deg, #FF9933 , #FFFFF1 , #138808 , #FF9933 , #FFFFF1 , #138808, #FF9933 , #FFFFF1 , #138808)",
    backgroundSize: "200%",
    WebkitBackgroundClip: "text !important",
    backgroundClip: "text !important",
    color: "transparent",
    animation: `$myEffect 3000ms ${theme.transitions.easing.easeInOut} infinite`,
  },
  "@keyframes myEffect": {
    from: {
      backgroundPositionX: "0px",
    },
    to: {
      backgroundPositionX: "-360px",
    },
  },
  enter: {
    color: "#ffff",
    fontFamily: "Nunito",
  },
  button: {
    color: "#5AFF3D",
    borderRadius: "50px",
  },
  icon: {
    color: "#fff",
    fontSize: "2rem",
  },
  colorText: {
    color: "#5AFF3D",
  },
  container: {
    textAlign: "center",
  },
  title: {
    color: "#fff",
    fontSize: "4.5rem",
  },
  goDown: {
    color: "#5AFF3D",
    fontSize: "1.5rem",
  },
}));
export default function Header() {
  const classes = useStyles();
  const [checked, setChecked] = useState(false);
  useEffect(() => {
    setChecked(true);
  }, []);
  return (
    <div className={classes.root} id="header">
      <Collapse
        in={checked}
        {...(checked ? { timeout: 1000 } : {})}
        collapsedHeight={50}
      >
        <div className={classes.container}>
          <h1 className={classes.title} color="primary">
            Welcome to <br />
            {/* Travel<span className={classes.colorText}>India.</span> */}
            <div className={classes.changeColor}>Travel India.</div>
          </h1>
          {/* <Scroll to="state-of-india" smooth={true}> */}
          <Button className={classes.button} variant="outlined" color="primary">
            <h3 className={classes.enter}>Enter</h3>
            <IconButton>
              <InfoIcon className={classes.goDown} fontSize="small" />
            </IconButton>
          </Button>
          {/* </Scroll> */}
        </div>
      </Collapse>
      {/* <StatesOfIndia /> */}
    </div>
  );
}

// import InfoIcon from '@material-ui/icons/Info';
