import React, { useState, useEffect } from "react";

import { useDispatch, useSelector } from "react-redux";
import Product from "./Places";
import axios from "axios";
import { listStateDetails } from "../actions/stateActions";

import { makeStyles, CircularProgress } from "@material-ui/core";
import ImageCard from "./ImageCard";
// import places from "../static/places";
import useWindowPosition from "../hook/useWindowPosition";
const useStyles = makeStyles((theme) => ({
  root: {
    minHeight: "100vh",
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
    alignItems: "center",
    [theme.breakpoints.down("md")]: {
      flexDirection: "column",
    },
  },
}));
const Places = ({ match }) => {
  const classes = useStyles();
  const dispatch = useDispatch();
  let stateDetials;

  useEffect(() => {
    dispatch(listStateDetails(match.params.id));
    console.log(match.params.id);
  }, [stateDetials]);
  // }, [dispatch]);

  // const stateDetials = useSelector((state) => state.stateDetials);
  stateDetials = useSelector((state) => state.stateDetials);
  const { loading, error, states } = stateDetials;
  const places = states.places;
  console.log(JSON.stringify(stateDetials));
  // console.log(state);
  // const [expanded, setExpanded] = React.useState(false);

  // const dispatch = useDispatch();
  // const stateList = useSelector((state) => state.stateList);
  // const { loading, error, states } = stateList;

  // const classes = useStyles();

  // useEffect(() => {
  //   dispatch(listStates());
  // }, [dispatch]);
  // // }, []);
  // // }, [states]);

  const checked = useWindowPosition("header");
  console.log(useWindowPosition("header"));

  return (
    <>
      {loading ? (
        <CircularProgress />
      ) : error ? (
        <MuiAlert severity="error">{error}!</MuiAlert>
      ) : (
        <div className={classes.root} id="places">
          {places.map((place) => (
            // console.log(place);
            <ImageCard place={place} checked={checked} />
          ))}
        </div>
      )}
    </>
  );
};

export default Places;
