import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import Product from "../components/Places";
import axios from "axios";
import {
  Container,
  makeStyles,
  Paper,
  Grid,
  CircularProgress,
} from "@material-ui/core";
import { listStates } from "../actions/stateActions";
import MuiAlert from "@material-ui/lab/Alert";

const useStyles = makeStyles((theme) => ({
  root: {
    [theme.breakpoints.down("md")]: {
      flexDirection: "column",
    },
  },
  list: {
    display: "flex",
    padding: theme.spacing(2),
    marginTop: "2rem",
  },
}));

const HomeScreen = () => {
  const dispatch = useDispatch();
  const stateList = useSelector((state) => state.stateList);
  const { loading, error, states } = stateList;

  const classes = useStyles();

  useEffect(() => {
    dispatch(listStates());
  }, [dispatch]);
  // }, []);
  // }, [states]);

  return (
    <div className={classes.root}>
      <h1>Latest</h1>
      {loading ? (
        <CircularProgress />
      ) : error ? (
        <MuiAlert severity="error">{error}!</MuiAlert>
      ) : (
        <Grid container spacing={0} className={classes.list}>
          {states.map((state) => (
            <Grid item xs={12} md={6} lg={4} xl={3} key={state._id}>
              <Product state={state} />
            </Grid>
          ))}
        </Grid>
      )}
    </div>
  );
};

export default HomeScreen;
