import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { listStateDetails } from "../redux/stateActions";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import { makeStyles, CircularProgress } from "@material-ui/core";
import MuiAlert from "@material-ui/lab/Alert";
import ImageCard from "./ImageCard";
// import useWindowPosition from "../hook/useWindowPosition";

const useStyles = makeStyles((theme) => ({
  root: {
    minHeight: "100vh",
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
    alignItems: "center",
    [theme.breakpoints.down("md")]: {
      flexDirection: "column",
    },
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
    marginTop: "5rem",
    background: "white",
    padding: "1px",
    borderRadius: "3px",
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  dropdown: {
    // padding: "3rem",
  },
}));

const Places = ({ match }) => {
  const classes = useStyles();
  const dispatch = useDispatch();
  const [timer, setTimer] = useState(false);
  const [category, setCategory] = React.useState("");

  const handleChange = (event) => {
    setCategory(event.target.value);
  };

  setTimeout(() => {
    setTimer(true);
  }, 500);
  let stateDetials;

  useEffect(() => {
    dispatch(listStateDetails(match.params.id));
  }, [stateDetials]);
  // }, [dispatch]);

  stateDetials = useSelector((state) => state.stateDetials);
  let { loading, error, states } = stateDetials;
  let filteredPlaces;

  if (!loading) {
    let places = states.places;
    filteredPlaces = places.filter((place) => {
      if (category == "") {
        return true;
      } else if (category !== "" && category == place.category) {
        return true;
      }
    });
  }

  return (
    <>
      <div>
        <FormControl variant="filled" className={classes.formControl}>
          <InputLabel id="demo-simple-select-outlined-label">
            category
          </InputLabel>
          <Select value={category} onChange={handleChange} label="category">
            <MenuItem value="">
              <em>None</em>
            </MenuItem>
            <MenuItem value={"Sea"}>Sea</MenuItem>
            <MenuItem value={"Temple"}>Temple</MenuItem>
            <MenuItem value={"Forrest"}>Forrest</MenuItem>
            <MenuItem value={"Hill"}>Hill</MenuItem>
            <MenuItem value={"Valley"}>Valley</MenuItem>
            <MenuItem value={"Mountain"}>Mountain</MenuItem>
            <MenuItem value={"Historical"}>Historical</MenuItem>
          </Select>
        </FormControl>
      </div>

      {/* <CardTest /> */}

      <div>
        {loading ? (
          <CircularProgress />
        ) : error ? (
          <MuiAlert severity="error">{error}!</MuiAlert>
        ) : (
          <div className={classes.root} id="places">
            {
              // states.places.map((place) => (
              filteredPlaces.map((place) => (
                <ImageCard
                  key={place._id}
                  place={place}
                  checked={true}
                  goto={"PlacesDetails"}
                  stateId={states._id}
                />
              ))
            }
          </div>
        )}
      </div>
    </>
  );
};

export default Places;
