import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import Product from "./Places";
import axios from "axios";
import { listStateDetails } from "../actions/stateActions";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormHelperText from "@material-ui/core/FormHelperText";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import CardTest from "./CardTest";

import {
  makeStyles,
  CircularProgress,
  Button,
  Typography,
} from "@material-ui/core";
import MuiAlert from "@material-ui/lab/Alert";
import ImageCard from "./ImageCard";
// import places from "../static/places";
import useWindowPosition from "../hook/useWindowPosition";

const useStyles = makeStyles((theme) => ({
  root: {
    minHeight: "100vh",
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
    alignItems: "center",
    [theme.breakpoints.down("md")]: {
      flexDirection: "column",
    },
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
    marginTop: "5rem",
    background: "white",
    padding: "1px",

    borderRadius: "3px",
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  dropdown: {
    padding: "3rem",
  },
}));
const Places = ({ match }) => {
  const classes = useStyles();
  const dispatch = useDispatch();
  const [timer, setTimer] = useState(false);
  const [category, setCategory] = React.useState("");

  const handleChange = (event) => {
    setCategory(event.target.value);
  };
  setTimeout(() => {
    setTimer(true);
  }, 500);
  let stateDetials;

  useEffect(() => {
    dispatch(listStateDetails(match.params.id));
    console.log(`this is parameter variable ${match.params.id}`);
  }, [stateDetials]);
  // }, [dispatch]);

  // const stateDetials = useSelector((state) => state.stateDetials);
  stateDetials = useSelector((state) => state.stateDetials);
  const { loading, error, states } = stateDetials;
  // console.log(JSON.stringify(stateDetials));

  // const checked = useWindowPosition("header");
  // console.log(useWindowPosition("header"));

  return (
    <>
      <div>
        <FormControl variant="filled" className={classes.formControl}>
          <InputLabel id="demo-simple-select-outlined-label">
            category
          </InputLabel>
          <Select
            // labelId="demo-simple-select-outlined-label"
            // id="demo-simple-select-outlined"
            value={category}
            onChange={handleChange}
            label="category"
          >
            <MenuItem value="">
              <em>None</em>
            </MenuItem>
            <MenuItem value={"Sea"}>Sea</MenuItem>
            <MenuItem value={"Temple"}>Temple</MenuItem>
            <MenuItem value={"Forrest"}>Forrest</MenuItem>
            <MenuItem value={"Hill"}>Hill</MenuItem>
            <MenuItem value={"Valley"}>Valley</MenuItem>
            <MenuItem value={"Mountain"}>Mountain</MenuItem>
            <MenuItem value={"Historical"}>Historical</MenuItem>
          </Select>
        </FormControl>
      </div>

      {/* <CardTest /> */}
      <div className={classes.root}>
        <div>
          {loading ? (
            <CircularProgress />
          ) : error ? (
            <MuiAlert severity="error">{error}!</MuiAlert>
          ) : (
            <div className={classes.root} id="places">
              {states.places.map((place) => (
                // console.log(place)
                <ImageCard
                  place={place}
                  checked={true}
                  goto={"PlacesDetails"}
                  stateId={states._id}
                />
              ))}
            </div>
          )}
        </div>
      </div>
    </>
  );
};

export default Places;
