import React, { useState, useEffect } from "react";

import { useDispatch, useSelector } from "react-redux";
import Product from "../components/Product";
import axios from "axios";
import { listStates } from "../actions/stateActions";

import { makeStyles } from "@material-ui/core/styles";
import ImageCard from "./ImageCard";
import places from "../static/places";
import useWindowPosition from "../hook/useWindowPosition";
const useStyles = makeStyles((theme) => ({
  root: {
    minHeight: "100vh",
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
    alignItems: "center",
    [theme.breakpoints.down("md")]: {
      flexDirection: "column",
    },
  },
}));
const StatesOfIndia = () => {
  const classes = useStyles();
  const checked = useWindowPosition("header");
  console.log(useWindowPosition("header"));

  return (
    <div className={classes.root} id="state-of-india">
      <ImageCard place={places[0]} checked={checked} />
      <ImageCard place={places[1]} checked={checked} />
      <ImageCard place={places[2]} checked={checked} />
      <ImageCard place={places[3]} checked={checked} />
    </div>
  );
};

export default StatesOfIndia;
