import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import Product from "./Places";
import axios from "axios";
import { listStateDetails } from "../actions/stateActions";

import {
  makeStyles,
  CircularProgress,
  Button,
  Typography,
} from "@material-ui/core";
import MuiAlert from "@material-ui/lab/Alert";
import ImageCard from "./ImageCard";
// import places from "../static/places";
import useWindowPosition from "../hook/useWindowPosition";
const useStyles = makeStyles((theme) => ({
  root: {
    minHeight: "100vh",
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
    alignItems: "center",
    [theme.breakpoints.down("md")]: {
      flexDirection: "column",
    },
  },
}));
const Places = ({ match }) => {
  const classes = useStyles();
  const dispatch = useDispatch();
  const [timer, setTimer] = useState(false);
  setTimeout(() => {
    setTimer(true);
  }, 500);
  let stateDetials;

  useEffect(() => {
    dispatch(listStateDetails(match.params.id));
    console.log(`this is parameter variable ${match.params.id}`);
  }, [stateDetials]);
  // }, [dispatch]);

  // const stateDetials = useSelector((state) => state.stateDetials);
  stateDetials = useSelector((state) => state.stateDetials);
  const { loading, error, states } = stateDetials;
  // console.log(JSON.stringify(stateDetials));

  // const checked = useWindowPosition("header");
  // console.log(useWindowPosition("header"));

  return (
    <>
      <div>
        <Typography
          gutterBottom
          variant="h5"
          // component="h1"
          className={classes.title}
        >
          Places to visit
        </Typography>
        <Button variant="outlined" color="primary">
          <Link
            to={`/states`}
            style={{ color: "inherit", textDecoration: "inherit" }}
          >
            Go back
          </Link>
        </Button>
      </div>
      <div className={classes.root}>
        <div>
          {loading ? (
            <CircularProgress />
          ) : error ? (
            <MuiAlert severity="error">{error}!</MuiAlert>
          ) : (
            <div className={classes.root} id="places">
              {states.places.map((place) => (
                // console.log(place);
                <ImageCard place={place} checked={true} />
              ))}
            </div>
          )}
        </div>
      </div>
    </>
  );
};

export default Places;
